<div class="block author full">	<span class="author-name">	<?php 	if ($user['enabledprofile'])	{		echo '<a href="'.base_url().'profile/'.$user['urlname'].'">'.$user['displayname'].'</a>';	}	else	{		echo $user['displayname'];	}	?>	</span>	<p class="author-profile">		<?php		echo generate_profile_picture("small", $user);		echo character_limiter(preg_replace("/<img[^>]+\>/i", "", $user['profile']), 1200, "... <a href=\"".base_url()."profile/".$user['urlname']."\">Continue reading</a>")?>	</p></div>