<div class="block author-new full">	<?php	echo generate_profile_picture("small", $user);	?>	<span class="username">	<?php	if ($user['enabledprofile'])	{		echo '<a href="'.base_url().'profile/'.$user['urlname'].'">'.$user['displayname'].'</a>';	}	else	{		echo $user['displayname'];	}	?>	</span>	<span class="started-writing">Started writing for us on <?php echo date("F jS, Y", strtotime($author['date']));?></span>	<span class="first-post">First post: <a href="<?php base_url().$author['posturlname'];?>"><?php echo $author['title'];?></a></span>	<span class="comment">	<?php echo $author['commentcount'];?>	<?php		if ($author['commentcount'] == 1)		{			echo ' comment';		}		else		{			echo ' comments';		}	?>	</span></div>